import React, { useState, useEffect } from 'react';
import { Trash2, Edit2, Plus, Search } from 'lucide-react';
import Modal from './Modal';
import api from '../api';

export default function SectionManagement() {
  const [sections, setSections] = useState([]);
  const [students, setStudents] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [formData, setFormData] = useState({
    student_id: '',
    teacher_id: '',
    class_id: '',
    classSection: ''
  });
  const [editingId, setEditingId] = useState(null);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [sectionData, studentData, teacherData, classData] = await Promise.all([
        api.getSections(),
        api.getStudents(),
        api.getTeachers(),
        api.getClasses()
      ]);
      setSections(sectionData);
      setStudents(studentData);
      setTeachers(teacherData);
      setClasses(classData);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.student_id || !formData.teacher_id || !formData.class_id || !formData.classSection.trim()) {
      alert('Please fill all fields');
      return;
    }
    try {
      if (editingId) {
        await api.updateSection(editingId, formData);
        setEditingId(null);
      } else {
        await api.createSection(formData);
      }
      setFormData({ student_id: '', teacher_id: '', class_id: '', classSection: '' });
      loadData();
    } catch (error) {
      alert('Error: ' + error.message);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure?')) {
      try {
        await api.deleteSection(id);
        loadData();
      } catch (error) {
        alert('Error: ' + error.message);
      }
    }
  };

  const handleEdit = (section) => {
    setFormData({
      student_id: section.student_id,
      teacher_id: section.teacher_id,
      class_id: section.class_id,
      classSection: section.classSection
    });
    setEditingId(section.classsection_ID);
  };

  const handleSearch = async (e) => {
    const value = e.target.value;
    setSearch(value);
    if (value.trim()) {
      try {
        const data = await api.searchSections(value);
        setSections(data);
      } catch (error) {
        console.error('Search error:', error);
      }
    } else {
      loadData();
    }
  };

  return (
    <div className="p-6 fade-in">
      <div className="mb-8">
        <h1 className="text-4xl font-bold text-gray-800 mb-2">Class Enrollment Management</h1>
        <p className="text-gray-600">Manage student class enrollments and assignments</p>
      </div>

      {/* Form */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
        <h2 className="text-2xl font-bold text-gray-800 mb-4">
          {editingId ? 'Edit Enrollment' : 'Add New Enrollment'}
        </h2>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-6 gap-3">
          <select
            value={formData.student_id}
            onChange={(e) => setFormData({ ...formData, student_id: e.target.value })}
            className="input-field"
          >
            <option value="">Select Student</option>
            {students.map(s => (
              <option key={s.student_id} value={s.student_id}>
                {s.student_FName} {s.student_LName}
              </option>
            ))}
          </select>
          <select
            value={formData.teacher_id}
            onChange={(e) => setFormData({ ...formData, teacher_id: e.target.value })}
            className="input-field"
          >
            <option value="">Select Teacher</option>
            {teachers.map(t => (
              <option key={t.teacher_id} value={t.teacher_id}>
                {t.teacher_Fname} {t.teacher_Lname}
              </option>
            ))}
          </select>
          <select
            value={formData.class_id}
            onChange={(e) => setFormData({ ...formData, class_id: e.target.value })}
            className="input-field"
          >
            <option value="">Select Class</option>
            {classes.map(c => (
              <option key={c.class_id} value={c.class_id}>{c.class_desc}</option>
            ))}
          </select>
          <input
            type="text"
            placeholder="Section (e.g., BSIT-1A)"
            value={formData.classSection}
            onChange={(e) => setFormData({ ...formData, classSection: e.target.value })}
            className="input-field"
          />
          <button type="submit" className="btn-primary flex items-center justify-center gap-2 col-span-1">
            <Plus size={20} />
            {editingId ? 'Update' : 'Add'}
          </button>
          {editingId && (
            <button
              type="button"
              onClick={() => {
                setEditingId(null);
                setFormData({ student_id: '', teacher_id: '', class_id: '', classSection: '' });
              }}
              className="btn-secondary"
            >
              Cancel
            </button>
          )}
        </form>
      </div>

      {/* Search */}
      <div className="mb-6">
        <div className="flex items-center gap-2 bg-white p-3 rounded-lg shadow-sm">
          <Search size={20} className="text-gray-400" />
          <input
            type="text"
            placeholder="Search enrollments..."
            value={search}
            onChange={handleSearch}
            className="flex-1 outline-none bg-transparent"
          />
        </div>
      </div>

      {/* Table */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden overflow-x-auto">
        {loading ? (
          <div className="p-8 text-center text-gray-500">Loading...</div>
        ) : sections.length === 0 ? (
          <div className="p-8 text-center text-gray-500">No enrollments found</div>
        ) : (
          <table className="w-full min-w-max text-sm">
            <thead className="bg-purple-600 text-white">
              <tr>
                <th className="px-4 py-3 text-left font-semibold">Class</th>
                <th className="px-4 py-3 text-left font-semibold">Course</th>
                <th className="px-4 py-3 text-left font-semibold">Student</th>
                <th className="px-4 py-3 text-left font-semibold">Degree</th>
                <th className="px-4 py-3 text-left font-semibold">Teacher</th>
                <th className="px-4 py-3 text-left font-semibold">Section</th>
                <th className="px-4 py-3 text-center font-semibold">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {sections.map((section) => (
                <tr key={section.classsection_ID} className="hover:bg-blue-50 transition-colors">
                  <td className="px-4 py-4 text-gray-700 font-semibold">{section.class_desc}</td>
                  <td className="px-4 py-4 text-gray-700">{section.course_desc}</td>
                  <td className="px-4 py-4 text-gray-700">{section.student_FName} {section.student_LName}</td>
                  <td className="px-4 py-4 text-gray-700">{section.degree_desc}</td>
                  <td className="px-4 py-4 text-gray-700">{section.teacher_Fname} {section.teacher_Lname}</td>
                  <td className="px-4 py-4 text-gray-700 font-semibold">{section.classSection}</td>
                  <td className="px-4 py-4">
                    <div className="flex justify-center gap-2">
                      <button
                        onClick={() => handleEdit(section)}
                        className="text-blue-600 hover:text-blue-800"
                      >
                        <Edit2 size={18} />
                      </button>
                      <button
                        onClick={() => handleDelete(section.classsection_ID)}
                        className="text-red-600 hover:text-red-800"
                      >
                        <Trash2 size={18} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
